css:
@-moz-document url("https://www.litecoinpool.org/account") {
html, iframe, frame {background: black; -webkit-filter: invert(1) hue-rotate(180deg) !important}

img,
embed[flashvars],
*[type*="application/x-shockwave-flash"],
*[type*="application/x-silverlight"],
div[class="html5-video-container"]    
{-webkit-filter: invert(1) hue-rotate(180deg) !important}

#header {
    overflow: auto;
    background-color: #fff;
    color: #6bff8c;
    padding: .5em 1em;
}
#topstats th {
    padding: 0 0 0 1.5em;
    font-weight: normal;
    color: #004a8c;
}
#topstats td {
    padding: 0 0 0 .5em;
    color: #713407;
}
#menu {
    margin: 0;
    background-color: #b1c0d2;
    color: #9297e0;
    list-style-type: none;
    text-align: center;
    padding: .5em 1em;
    border-top: 1px solid #333;
    border-bottom: 1px solid #f80;
}
#topnews {
    background-color: #8b9ba2;
    color: #bb9595;
    padding: .2em 1.5em;
    text-align: right;
    font-size: .7em;
    border-top: 1px solid #0c0c0c;
}
#header a, #menu a, #topnews a {
    color: #000000;
}

a {
    color: #f37101;
    text-decoration: none;
}
}
