css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mangatown.com") {
  header .menu-page,
  header .menu-fixed,
  .gray-bg,
  .article_content,
  footer {
      background: #171b1c;
  }
  header .menu-page {
      border-bottom: 1px solid #3e3e3e;
  }
  .article_content {
      border-right: 1px solid #3e3e3e;
  }
  footer {
      border-top: 1px solid #3e3e3e;
  }
  li a,
  .title-top {
      color: #fff !important;
  }
  a.logo {
      filter: invert(100%);
  }
  .fb_comment,
  .mt10 {
      color: #fff !important;
  }
  .cartoon-list li a,
  ul.main-nav li a {
      color: #000 !important;
  }
}
