css:
@-moz-document domain("pancernik.info") {
.navbar {
    background-color: #171719;
    line-height: 20px;
    font-size: 18px;
    height: 20px;
    overflow: hidden;
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    z-index: 5;
}
.container>.side {
    flex: 1;
    position: relative;
    padding-top: 20px;
}
.chat,
.player {
    position: absolute;
    top: 20px;
    left: 0;
    bottom: 0;
    right: 0;
    background-color: #000;
}
element.style {
    height: 20px;
}
.navbar-navigation .channel-info>li:first-child {
    padding-top: 0;
}
}
