css:
@-moz-document domain(anime-planet.com) {
  
body {
  color: #ababab;
  background: #5c5c5c;
}

a {
  color: #00a2ff; 
}
h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {
  color: #00a2ff; 
}

button, .button {
  background-color: #00a2ff;
}

#siteHeader {
  background: #303030;
  border-top: 4px solid #00a2ff;
}
.new-feature::before {
   background: #00a2ff;
}
.loggedIn a {
  color: #ebebeb;
}
#siteLogo {
  color: #00a2ff;
}
.loggedInOptions {
  color: #ebebeb;
  background: #5c5c5c;
}
#siteNav ul li a{
  color: #ebebeb;
}
#siteNav ul, #siteNav ul ul {
  background: #303030;
}
.highlight-new { /*Menu Style */
    background: #f9f8f4;
    font-family: lato-bold;
    border-top: 1px solid #00a2ff;
    border-bottom: 1px solid #00a2ff;
}

#siteContainer {
  background: #383838;
}
.bg-themeColor {
    background: #525252;
}

.sb-theme {
    background: #00a2ff url('https://i.imgur.com/Gq9UtE1.jpg') 50% 50%;
}

.card h4 {
  color: #ababab;
}

.bx-controls a {
  color: #00a2ff;
}
  
}
