css:
@-moz-document domain("apollographql.com") {
.content, section, section.light.shaded-top-bottom, section.borealis, section.charcoal, section.corona, section.navy, section.violet, section.azure, .btn.tertiary:hover, .content .api {
    background-color: #121212;
    background-image: none;
}

.header-content, pre, code, .content .api .api-heading {
    background-color: #212121;
    border-color: #212121;
}

.btn.tertiary {
    background-color: #303030;
}

h1, h2, h3, em, .document-formatting h2, .document-formatting h3, .btn.tertiary {
    color: #FFF;
}

body, pre {
    color: rgba(255, 255, 255, .7);
}

.link.tertiary, .content .api .api-heading .subtext-api .src-code, .content .api .api-body dl dt .type {
    color: rgba(255, 255, 255, .5);
}
}
