css:
@-moz-document domain("reactnavigation.org") {
body, .mainContainer, .homeContainer {
    background-color: #121212;
}


pre code, footer.nav-footer, nav.toc .toggleNav .navGroup.navGroupActive ul {
    background-color: #212121;
}


p, li, code[class*='language-'], pre[class*='language-'] {
    color: rgba(255, 255, 255, .7);
}

nav.toc .toggleNav ul li a {
    color: #FFF;
}
}
