css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("manga-town.net") {
html.wf-fontawesome-n4-active.wf-active body div#content div#main-content div.thumbs div.c{
  transition: 0.15s;
  }
  html.wf-fontawesome-n4-active.wf-active body div#content div#main-content div.thumbs div.c:hover{
				-webkit-transform: scale(1.1);
		-moz-transform: scale(1.1);
		-ms-transform: scale(1.1);
		-o-transform: scale(1.1);
		transform: scale(1.1);
}
  html.wf-fontawesome-n4-active.wf-active body div#content div#main-content div#detail section#thumbnail{
  transition: 0.15s;
  }
  html.wf-fontawesome-n4-active.wf-active body div#content div#main-content div#detail section#thumbnail:hover{
				-webkit-transform: scale(1.1);
		-moz-transform: scale(1.1);
		-ms-transform: scale(1.1);
		-o-transform: scale(1.1);
		transform: scale(1.1);
  }
  html.wf-fontawesome-n4-active.wf-active body div#content div#main-content div.c{
  transition: 0.15s;
  }
  html.wf-fontawesome-n4-active.wf-active body div#content div#main-content div.c:hover{
				-webkit-transform: scale(1.1);
		-moz-transform: scale(1.1);
		-ms-transform: scale(1.1);
		-o-transform: scale(1.1);
		transform: scale(1.1);
}
}
