css:
@-moz-document url-prefix(https://docs.nativebase.io/)
{
 .book-summary{
    background: #292929  !important;
    color: #f9f9f9 !important;
 }

 .book-body, .markdown-section table th, .markdown-section table tr,  .markdown-section table tr:nth-child(2n){
    background: #191919  !important;
    color: #f9f9f9 !important;
 }

.book-summary ul.summary li a, .book-summary ul.summary li span{
    color: #f9f9f9 !important;
}

 .markdown-section{
   color: #fff;
 }

.book .book-body .page-wrapper .page-inner section.normal a, .book .book-summary ul.summary li.active>a{
color: #6BAF9A !important;
}

.language-jsx{
}

.book .book-body .page-wrapper .page-inner section.normal pre, .book .book-body .page-wrapper .page-inner section.normal code{
background-color: #090909 !important;
}

 .markdown-section code{
  background-color: transparent !important;
  box-shadow: none !important;
  color: #66ff00 !important;
  }
}
