css:
@-moz-document url-prefix("https://www.thescore.com/nba/events") {
body,
html {
    background: #0c0d0e;
}
.Layout__container--1pk24 {
    background: #000!important;
}
.card.jsx-3553219539 {
    background-color: #0c0d0e;
}
.bg-backdrop {
    background-color: #1e1f21;
}
.horizontal-scroll .shadow-overlay {
    background-color: #1e1f21;
}
.horizontal-scroll .shadow-overlay {
    -webkit-box-shadow: 0 0 20px 20px #1e1f21;
    -moz-box-shadow: 0 0 20px 20px #1e1f21;
    box-shadow: 0 0 20px 20px #1e1f21;
}
.main-content {
    min-height: 464px;
    border: #000000 1px solid;
    border-bottom: none;
}
h1 {
    color: #939393 !important;
}
.font-regular {
    color: black !important;
}
.context-menu {
    background: #000 !important;
    color: #939393 !important;
}
.context-menu-row {
    background: #000 !important;
}
.heading-h2,
.list-view-heading,
.table .table-heading-h2 > tr > th,
.article-content .table-responsive table .table-heading-h2 > tr > th,
.table .article-content .table-responsive thead > tr > th,
.article-content .table-responsive .table thead > tr > th,
.article-content .table-responsive table thead > tr > th {
    background: #0c0d0e !important;
}
.horizontal-scroll .scrollable-content {
    background-color: #0c0d0e;
}
.tab-bar-wrapper .scrollable-content .tab-bar-item a {
    color: #939393 !important;
}
.tab-bar-wrapper .scrollable-content .tab-bar-item a:active,
.tab-bar-wrapper .scrollable-content .tab-bar-item a.active {
    color: #3371c0 !important;
}
.bg-component-backdrop,
.bordered-box-xs,
.info-segment,
.bordered-box-sm {
    background-color: #939393 !important;
    border-color: black !important;
}
.list-view-item,
.list-view-item-headshot,
.list-view-item-multiline,
.box-score .boxscore-segment,
.plays .boxscore-segment,
.stats .boxscore-segment,
.penalties .boxscore-segment {
    background: #939393;
    border-color: black !important;
}
.app-header .header-title {
    border-right: #000 solid;
}
.table-responsive {
    background: #939393;
}
.table-responsive .table td,
.article-content .table-responsive table td {
    border-top: none;
    border-bottom: #000000 1px solid;
}
.heading-h3 {
    color: #939393;
}
.search-box--closed .search__button--toggle {
    background: #000000;
}
.get-the-app-link {
    visibility: hidden !important;
}
.footer__get-the-app {
    visibility: hidden !important;
}
::-webkit-scrollbar {
    background: #939393 !Important;
}
::-webkit-scrollbar-track {
    background: #0c0d0e !important;
}
::-webkit-scrollbar-thumb {
    background: #1e1f21;
}
}
