css:
@-moz-document domain("poetryfoundation.org") {
.o-masthead {
    position: absolute;
}

.isScrolled .o-masthead, .o-masthead.isScrolled, .o-mastheadSmall-drawer.isActive {
    box-shadow: none;
}

.o-masthead-inner:before {
    display: none;
}

.o-masthead-inner {
    min-height: 0;
    height: 100px;
}

.o-masthead-navigation {
    padding-bottom: 5px;
}

.o-masthead-tag {
    display: none;
}

.c-primaryNav {
    display: none;
}

.c-utilityNav li:last-of-type {
    display: none;
}

.o-masthead-branding svg {
    top: 25px;
}
}
