css:
@-moz-document url-prefix("https://admin.wayfair.com/v/tracker/"), url-prefix("https://admin.wayfair.com/tracker/views/147.php") {
  .js-logged {
    display: none;
  }

  .js-estimate strong {
    display: none;
  }

  .js-estimate:before {
    content: "Pts:";
    font-weight: bold;
  }

  .js-update-ticket {
    display: none;
  }

  .workboard_type_icon {
    font-size: 16px;
  }

  .js-show-ticket {
    font-size: 14px;
    text-decoration: none;
    padding-top: 8px;
  }

  #workboard .column .col_header {
    background: #6699a6;
     font-size: 11px;
  }

  .js-count {
    font-weight: bold;
  }

  #mainform > .textblock .centertext.midtitle {
    display: none;
  }

  .column:first-child:nth-last-child(4) {
      margin: 0 .65% .5% 0;
  }
  
  .column:first-child:nth-last-child(4) ~ .column {
      margin: 0 .65% .5%;
  }
  
  .column:first-child:nth-last-child(5) {
      margin: 0 .625% .5% 0;
  }
  
  .column:first-child:nth-last-child(5) ~ .column {
      margin: 0 .625% .5%;
  }
  
  .column:first-child:nth-last-child(n) ~ .column:last-child {
      margin-right: 0;
  }

  .column ol.tickets.ui-sortable {
    border: 0;
  }

  .column li.ticket {
    border: 0;
    background: #f0f0f0;
    margin: 8px 0;
  }

  li .dots {
    background-image: none;
  }

  .tickets .emp_placeholder_img {
    border: 0;
    background: #e4e4e5;
  }


  .epic .epic_start {
    background: #e4e4e5 !important;
  }

  .epic .epic_tail {
    display: none;
  }

  .ticket_dets .workboard_type_icon {
    font-size: 18px;
  }
}
