css:
@-moz-document domain("the-digital-picture.com") {
body,
#EntirePage,
.SocialFloatTop,
#Header,
.MainMenu .SubMenu ul li a,
#Footer,
#ReviewWidgetScroller
{
    background-color: #111;
}

.SecNav,
.SecNav ul,
.MouseOverBackground,
.MainMenu .SubMenu ul li a:hover,
.BlockMenu .BlockSubMenu ul li:nth-child(even),
.SpecsTableHeader
{
    background-color: #222 !important;
}

.ComparisonTableSelected
{
    background-color: #211 !important;
}

body,
.SecNav li,
.SecNav A:link,
a:visited,
td,
ul,
.BlockSubMenu A:link,
.MainMenu .SubMenu ul li a
{
    color: #909090 !important;
}

.SecNav A:hover,
.BlockSubMenu A:hover,
a:hover
{
    color: red !important;
}

a:link,
.MouseOverBackground A:link,
.Category a:link,
.SiteIndexLinks A:link,
.NewsNav A:link
{
    color: #ccc;
}

.ComparisonTableHeader td,
a.ButtonBuy.ButtonBuyLarge:hover,
.BlockMenuTitleAttn A:hover
{
    color: #ccc !important;
}

a.ButtonBuy.ButtonBuyLarge,
.BlockMenuTitleAttn A
{
    color: #fff !important;
}

.MainLogo
{
    filter: invert(93.5%) grayscale(100%);
}

.MainMenu .SubMenu ul,
.SpecsTableHeaderCell,
.SpecsTable
{
    border-color: #222;
}
}
