css:
@-moz-document url-prefix("https://www.brickplanet.com/forum/thread/") {
.forum-thread-header {
    background: #3adb76;
    padding: 6px 0;
    font-size: 24px;
    color: #000000;
    line-height: 1.5;
    margin-top: 15px;
}
}

@-moz-document url-prefix("https://www.brickplanet.com/forum/") {
.container-header {
    background: #3adb76;
    color: #353535;
    border-radius: 5px 5px 0 0;
    font-size: 24px;
}
}

@-moz-document url-prefix("https://www.brickplanet.com/forum/") {
.forum-topic-container .thread-content-title a {
    color: #ffae00;
    word-break: break-word;
    line-height: 1.5;
}
}

@-moz-document url-prefix("https://www.brickplanet.com/forum/thread/") {
.forum-thread-header a {
    color: #ffae00;
    font-weight: 600;
}
}

@-moz-document url-prefix("https://www.brickplanet.com/forum/thread/") {
a {
    color: #ffae00;
}
}

@-moz-document url-prefix("https://www.brickplanet.com/users/") {
html, body {
    font-family: Open Sans, Helvetica, Arial, sans-serif;
    background: #27282C;
    color: #ffae00;
    height: 100%;
    padding: 0;
    margin: 0;
    font-size: 14px;
}
}

@-moz-document url("https://www.brickplanet.com/user/suspended/") {
.container-header {
    background: #3adb76;
    color: white;
    border-radius: 5px 5px 0 0;
    font-size: 16px;
}
}
