css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("avxhome.in"), domain("avxsearch.in") {

html, body, .container, .pagination {
  background: #222 !important;
  color: #ccc !important;
}
.search-form {
  background-image: none !important;
}
div#main-categories ul li a, div#main-categories-bottom ul li a{
  color: #ddd !important;
}
span.req {
color: #fff !important;
}
table.jCalendar th {
  background: #888 !important;
  color: #000 !important;
}
table.jCalendar td {
  background: #222 !important;
  color: #eee !important;
}
table.jCalendar td.other-month {
  color: #bbb !important;
}
table.jCalendar td.today a, table.jCalendar td.today {
  background: #bbb !important;
  color: #222 !important;
}
a {
  color: #ddd !important;
}
form label {
  color: #ddd !important;
}
label {
  color: #ddd !important;
}
.container-text {
  background: #444 !important;
  width: 122% !important;
  margin-left: -38px !important;
}
table.quote {
  background: #222 !important;
  color: #ddd !important;
  border: 1px dotted #ddd !important;
}
input, button, .input-group-addon, .main-menu li, .main-menu li a, .main-menu li a span, .breadcrumb, .menu-button, .search_button {
  background-color: #222 !important;
  color: #aaa !important;
}
.main-menu li a, .search_button {
  border: 1px solid #aaa !important;
}
.main-menu li a:hover, input:hover, button:hover, .input-group-addon:hover, .search_button:hover {
  border: 1px solid #fff !important;
}
.form-control:focus {
    box-shadow: none !important;
}
.form-control {
    color: #aaa !important;
    background-color: #222 !important;
    border: 1px solid #aaa !important;
}
}
