@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('dogpile.com')

/* Hide All Ads */
/* Dogpile Search - Jim Can Read It! */
/* Version 0.1 - 03/09/2018 */
    
{   
	div#rightColumn, 
    .searchResult.webResult.vigLinkResult, 
    div#resultsBottom, 
    div#resultsShopping, 
    div#resultsAdsTop, 
    .searchResult.adResult.mainAd  
    {
    display: none !important; 
    visibility: hidden !important;
    height: 0 !important;
    width: 0 !important;
	}
}
