css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
url("http://b.hatena.ne.jp/entrylist"),
url-prefix("http://b.hatena.ne.jp/entrylist?"),
url-prefix("http://b.hatena.ne.jp/keyword/"){
.selector > li:first-child						{-moz-box-ordinal-group:1!important;}
.selector > li:first-child + li					{-moz-box-ordinal-group:3!important;}
.selector > li:first-child + li + li				{-moz-box-ordinal-group:4!important;}
.selector > li:first-child + li + li + li			{-moz-box-ordinal-group:2!important;}
.selector > li:first-child + li + li + li + li			{-moz-box-ordinal-group:5!important;}
.selector > li:first-child + li + li + li + li + li		{-moz-box-ordinal-group:6!important;}
.selector > li:first-child + li + li + li + li + li	 + li	{-moz-box-ordinal-group:7!important;}

.selector{
  display:-moz-box!important;
}
}

@-moz-document 
url-prefix("http://b.hatena.ne.jp/entrylist/"),
url-prefix("http://b.hatena.ne.jp/t/"){
.selector > li:first-child						{-moz-box-ordinal-group:1!important;}
/*
.selector > li:first-child + li					{-moz-box-ordinal-group:3!important;}
*/
.selector > li:first-child + li					{display:none!important;}
.selector > li:first-child + li + li				{-moz-box-ordinal-group:3!important;}
.selector > li:first-child + li + li + li			{-moz-box-ordinal-group:4!important;}
.selector > li:first-child + li + li + li + li			{-moz-box-ordinal-group:2!important;}
.selector > li:first-child + li + li + li + li + li		{-moz-box-ordinal-group:5!important;}

.selector{
  display:-moz-box!important;
}
}
