css:
@-moz-document domain("closed.pizza") {
#global-menu #global-menu-logo {
    background: url('https://i.imgur.com/mUWNmyq.png') center no-repeat;
}

#global-menu #global-menu-logo img {
    visibility: hidden;
}
.guest #global-menu-login input {
    visibility: hidden;
}
.guest #global-menu-login {
     background: url('https://i.imgur.com/0hzbF3p.png') center no-repeat;
}
#splatoon{
    display:block;
    position:fixed;
    width:100px;
    height:100px;
    top:0px;
    left:0px;
    z-index:2000;
    background:transparent url("https://i.imgur.com/1PFDC34.png") 0 0 no-repeat;animation:woomy 0.36s steps(17) infinite;
}
@keyframes woomy{
    100%{
    background-position:0 -1700px;}
}
#nprogress{
    pointer-events:none;
}
#nprogress .bar{
    background:linear-gradient(45deg,#322ff3,#b50096);
    position:fixed;z-index:1031;
    top:0;
    left:0;
    width:100%;
    height:4px;
}
#nprogress .peg{
    display:block;
    position:absolute;
    right:0px;
    width:100px;
    height:100%;
    box-shadow:0 0 10px #b50096,0 0 4px #b50096;
    opacity:1.0;-webkit-transform:rotate(3deg) translate(0px,-4px);
    -ms-transform:rotate(3deg) translate(0px,-4px);
    transform:rotate(3deg) translate(0px,-4px);
}
.nprogress-custom-parent{
    overflow:hidden;
    position:relative;
}
.nprogress-custom-parent #nprogress .spinner,.nprogress-custom-parent #nprogress .bar{
    position:absolute;
}
}
