css:
@-moz-document domain("crucible") {
div#content > div#content-resizable {
    width: 100px !important;
}
div#content > div#content-resizable.collapsed {
    width: 25px !important;
}
div#content div#content-resizable:hover div#navigation-tree {
    position: inherit;
}
div#content > div#content-resizable.collapsed:hover {
    width: 30px !important;
}
div#content > div#content-resizable:hover {
    width: 250px !important;
}
div#content div.edit-mode-help {
    height: 70px;
    overflow-y: auto;
    overflow-x: hidden;
}
div#content div#content-resizable div#navigation-tree,
div#content div#content-resizable ul.tree a {
    font-size: 10px !important;
}
div#content div#content-resizable:hover div#navigation-tree,
div#content div#content-resizable:hover ul.tree a {
    font-size: 11px !important;
}
ul#frx-overview {
    height: 100px;
    overflow: hidden;
    width: 140px;
}
div#content div#content-resizable ul.tree li {
    position: relative;
    left: -14px;
}
div.ui-resizable-handle {
    display: none;
}
td#column-sidebar h3 {
    font-size: 13px;
}
td#column-sidebar ul {
    position: relative;
    left: -14px;
}
td#column-sidebar:not(:hover) {
    width: 115px !important;
    overflow: hidden;
    display: block;
    font-size: 10px;
}
div#content.collapsed-sidebar td#column-sidebar {
    width: 0px !important;
}
td#column-sidebar:hover {
    width: 200px !important;
    overflow: auto;
    display: block;
    font-size: 12px;
}
}
