css:
@-moz-document domain('censor.net.ua') {
body > div:nth-child(1),
body > header > aside,
body > div.wrap > div.wrapper > div.right > div,
.main > section > article > div.text > div.innerevents,
body > div.wrap > div.wrapper > div.main > div,
.news.full-view > .search,
.news.full-view > .full-compact,
.right > div,
.right > iframe,
.main > div,
.main > iframe
{
	background: none !important;
    display: none !important;
    height: 0 !important;
}
body > div.wrap > div.wrapper > div.main > div#photoshop {
	display: block !important;
    height: auto !important;
}
.news_paging{
	margin-bottom: 30px;
}
.wrapper > .right > iframe {
	height: 1px !important;
    overflow: hidden !important;
    display: none !important;
    visibility: hidden !important;
    opacity: 0 !important;
}

.widget_header {
	padding-top: 0;
    height: 167px;
}
.widget_header .mob_app,
.widget_header .ref_blogosfera_genshtab,
.widget_header .lang_switcher {
	position: static;
    display: inline;
}
.widget_header .mob_app li,
.widget_header .ref_blogosfera_genshtab li {
    list-style: none;
    text-align: left;
    float: none;
    display: inline;
}
.widget_header nav.menu {
    position: static;
}
.right > div.authblock {
	display: block !important;
}
