css:
@-moz-document domain("leagueofgraphs.com")
{
body {
    background: #000;
}

div#cdm-zone-04,div#comments,
div#cdm-zone-05,div#footer {
    display: none;
}

#pageContent {
    background-color: #000;
}

#filters-menu {
    background-color: #111;
    border-color: #333;
}

#championsFilter,#ratingFilter,#rolesFilter {
    border-left: 1px solid #333!important;
}


nav.top-bar {
    opacity: .5;
}

.box {
    background-color: #111;
    border-color: #383838;
}

.data_table th {
    background-color: #191919!important;
    border-color: #333!important;
}

.data_table tr:nth-child(2n) {
    background-color: #272727!important;
}

.data_table td {
    border-color: #333;
}

.see_more_button {
    opacity: .7;
}

.full-progress-bar {
    opacity: .5;
}

.comments-separator {
    border-top: 1px solid #444;
}

div#sidebar {
    opacity: .7;
}

.txt {
    opacity: .8;
}

.data_table td a:hover .name {
    text-decoration:  none;
    color: #aaa;
}
div.graph, .pie-chart  {
    filter: invert(1) hue-rotate(180deg);
}

nav.top-bar {
    backdrop-filter: blur(16px);
}
}
