/* Изменение цветов */
	/* цвет ответов и цитат  */
  		.post_block { background: #F0F0F0; }
  		.post_block h3 { background: #aaa; }
  		blockquote.ipsBlockquote { background: #fefefe; }
  		p.citation { background: #dfdfdf; }
	/* форма ответа */
  		.ipsBox_container {background: #F0F0F0; }
 		 body { background-color: #F0F0F0; color: #000;}
	/* фон */
  		.ipsBox {background: #dfdfdf; }
  		#content { background: #F0F0F0; }
    /* цвет текста*/ 
  		.post_body .post { color: #444;}  
   		a { color: #555; }
	/* Страница тем */
  		.unread { background-color: #F1F1F1; }
  		.row2, .post_block.row2 {background-color: #e0e0e0;}
  		.breadcrumb a {color: #000; font-size: 13px;}
  		.breadcrumb {color: #000}
  		.maintitle {background: #435267; color: #fff;}
		#content, .main_width { width: 99% !important; }

/* Лишнее уберем */
		h1.ipsType_pagetitle {display:none}
		div#ipbwrapper#div {display:none}
		div#index_stats {display:none}
		div#board_index.ipsLayout.ipsLayout_withright.ipsLayout_largeright.clearfix {padding-right: 0px}
}
