css:
@-moz-document domain("solo.nabiraem.ru") {
/* Reorder blocks */

.text-wrapper {
    display: flex;
    flex-direction: column-reverse;;
}

/* Change sizes blocks */

.text-real-value {
    height: 40px !important;
}

.text-wrapper>.text-prompt-value {
    height: 82px !important;
}

/* Change color completed text */

p .completed {
	color: #BBB !important;
}

/* Change color current key */

p>.nextkey {
    background: #cee6b3 !important;
    color: #000 !important;
}

/* Change color typed current key */

.text-real-value>p>.nextkey {
    background: #CCC !important;
}

/* Change opacity speed graph */

.speedchart>svg polygon {
    fill: rgba(97,173,36,0.1) !important;
}

/* Change font family typed text */

.text-wrapper p {
    font-family: DejaVu Sans Mono, Menlo, Lucida Console, monospace !important;
}

/* Hide unnecessary blocks */

.blocker, .block-silver, .lesson-block-caricature, .lesson-block-humor, .lesson-block-gymsoul, .lesson-block-onlinetest, .mixanatic, .mixdialog {
    display: none;
}
}
