css:
@-moz-document domain("bitbucket.org") {
body.adg3 ak-editor-bitbucket .code,
body.adg3 code,
body.adg3 pre {
    font-family: "Liberation Mono", "Roboto Mono", "Roboto Mono for Powerline", "Ubuntu Mono", Menlo, Courier, monospace;
}
body {
    font-family: "Liberation Sans", "Source Sans Pro", Roboto, Oxygen, "Droid Sans", sans-serif;
    font-size: 14px;
}
div .view-line {
    font-family: "Liberation Mono", "Roboto Mono", "Roboto Mono for Powerline", "Ubuntu Mono", Menlo, Courier, monospace;
}
}
