css:
@-moz-document domain("comments.bot") {
body {
    background: #424244;
}
.sg_gp_post,
.sg_gp_block_comments {
    background: #252525;
    box-shadow: none;
    color: white;
    border: 0px solid #dfe5ec !important;
    border-bottom: 1px solid #343535 !important;
}
.textarea,
.add-panel {
    color: white;
    background-color: #212020;
}
.textarea {
    border-bottom: 1px solid #343535;
}
.sg_gp_post_panel {
    padding: 10px 20px;
    margin-bottom: 5px;
}
.sg-btn:focus {
    outline: 0;
}
.sg_footer {
    color: #9a9a9a;
}
}
