css:
/* i really want this to be global */

@-moz-document domain('ikon.mn'), regexp('.*ikon.mn\\..*') {
    .op_name {
        color: #FFF !important;
    }
    html, body {
        background-color: #181818;
    }
    .lrcontent {
        background-color: #181818;
        color: #888;
    }
    h1 {
        color: #888;
    }
    /* Right List*/
    .op_list {
        color: #888 !important;
    }
    .mdl-textfield__label {
        color: #fff;
    }
    .lrhinner {
        background: #181818;
    }
    #wrapper {
        background-color: #181818;
    }
    .nldesc>.nltitle>a {
        color: #888;
    }
    .icontent {
        color: #888;
    }
    .ikoncomment .comment {
        color: #888;
    }
    element.style {
        color: #fff;
    }
    .hmenu {
        background: none;
    }
}
