css:
@-moz-document domain("www.ptt.cc") {
@font-face {
    font-family: WenQuan;
    src: url(https://github.jasonhk.net/Font-Improvements/ptt.cc/WenQuan.ttf);
}

#topbar {
    font-family: WenQuan, "文泉驛等寬正黑" !important;
}

#topbar .right.small {
	font-family: WenQuan, "文泉驛等寬正黑" !important;
}

#main-container, .nrec, .date, .author {
    font-family: WenQuan, "文泉驛等寬正黑" !important;
}

#main-content, #navigation {
    font-family: WenQuan, "文泉驛等寬正黑" !important;
}

.bbs-screen, .btn-big, .board-name, .hl, .board-class, .board-title {
    font-family: WenQuan, "文泉驛等寬正黑" !important;
}
}
