css:
@-moz-document url("http://172.20.205.51/dashboard"), url("http://172.20.205.52/dashboard") {
/*Body background color and default text color*/
body {
	background-color: #222;
    color: #eee;
}

/*Ericsson logo color*/
.mainHeader >div:first-of-type > div:nth-of-type(2) > svg > path {
	fill: #eee;
}

/*Advanced link*/
#mainHeaderAdvancedViewLink,
#mainHeaderAdvancedViewLink:visited,
#mainHeaderAdvancedViewLink:active,
#mainHeaderAdvancedViewLink:hover
{
	color: #eee;
}

/*Header preset button styling*/
.toolbarButton {
	background-color: #555;
    border: none;
    padding: 2px;
    margin-left: 10px;
    margin-top: 0px;
    text-shadow: none;
}
.toolbarButton.green {
	background-color: #666;
    border: none;
    padding: 2px;
    margin-left: 10px;
    margin-top: 0px;
    text-shadow: none;
}
.toolbarButton.green:hover {
	background-color: #666;
}

/*Alarm button*/
.alarmButton.green {
	background-color: darkgreen;
    border: none;
    padding: 8px;
    text-shadow: none;
}

.statusPane td, td:hover, tr:hover {
    color: #eee !important;
}
.statusPaneValue {
	color: #eee !important;
}

.paneHeaderGray {
	background-color: #555;
    color: #eee;
}
.paneWindow {
	background-color: #444;
    border: 4px solid transparent;
}

.paneWindow h1 {
	color: #eee;
}
/*Styling for input fields*/
input, select {
	background-color: #666;
    color: #eee;
    border: none;
    padding: 4px;
}
.userModified {
	background-color: #980;
}
.mainHeader > div[style] {
	
    background: none !important;
    background-color: #444 !important;
}

/*Pane header colors*/
.paneHeaderBlue {
	background-color: #333;
}
/*Pane expander arrow color*/
.paneHeaderExpander > svg > path {
    fill: #eee;
}
/*Preset pop-up*/
.settingsTable .rulerLine {
	background-color: #333;
}

/*Scroll bar color*/
::-webkit-scrollbar {
    height: 12px;
    width: 12px;
    background: #333;
}

::-webkit-scrollbar-thumb {
    background: #555;
    -webkit-border-radius: 0;
}

::-webkit-scrollbar-corner {
    background: #333;
}
}
