@-moz-document url-prefix("https://www.pinterest.co.uk/"), url-prefix("https://www.pinterest.com/") {
/* ==UserStyle==
@name Pinterest Anti-Nag
@namespace github.com/sbrl
@version 0.0.0
@description Hides the nags from pinterest to sign up
@author Starbeamrainbowlabs <feedback@starbeamrainbowlabs.com>
@homepageURL 
@supportURL 
@license CC-BY-SA-4.0
==/UserStyle== */

/** The "Login with Google box" */
.noButtonStyles + div {
    display: none;
}

iframe[src*="smartlock.google.com"] {
    display: none;
}

/** The full-screen nag */
[type="application/ld+json"] + div {
	display: none;
}

[data-reactid="5"] {
	position: static !important;
}

/** Another annoying full-screen nag  Gosh, they really don't want to make this easy, do they? :P **/
div[data-test-giftwrap="true"] {
	display: none;
}

/** The big blue bar at the top **/
.Closeup__wrapper > div:first-child {
	display: none;
}
}
