css:
@-moz-document domain("golang.org") {
pre, pre .ln, div.play .input, div.play .input textarea, div.play .output, div.play .output pre, div#learn .input, div#learn .input textarea, div#learn .output, div#learn .output pre {
    background: initial;
}

#file-editor, #file-editor .CodeMirror-lines, #file-editor .CodeMirror-gutters {
background: #fff;
}

pre .selection, pre .selection-comment {
    background: hsl(48, 25%, 82%);
}

div.play .input, div.play .output, div#learn .input, div#learn .output{
border: 1px solid #686e85;
}

pre .comment {
    color: #686e85;
}
}
