css:
@-moz-document domain("dbb.d3k.org"), domain("descentbb.net") {
.postprofile .avatar img, .no-avatar .avatar-container {
    display:none;
}

.online-ribbon span {
    opacity: 0.35;
    font-size: 0px;
    line-height: 10px;
    height: 16px;
    top: 4px;
    left: -35px;
}

.online-ribbon span + span {
    display: none;
}

body, html {
    background: #111;
}

#wrap {
    background: #0c0c0c;
}

#body-wrapper {
    background: linear-gradient(to bottom, rgba(17, 17, 17, 0.25) 0%, rgba(17, 17, 17, 1) 400px), #111 url("http://www.descentbb.net/styles/black/theme/images/pattern.png");
    border: 0px;
}

.forabg ul.topiclist li.row, .forumbg ul.topiclist li.row, .panel, .stat-block, .bg2, .bg1 {
    background: #111;
    box-shadow: 2px 2px 6px 0px #000;
}

fieldset.polls dl {
    border-top-color: #222;
    border-bottom-color: #070707;
}
}
