css:
@-moz-document domain(www.rohlik.cz) {
.product__name{ font-size: 12px !important; margin-bottom: 0 }
.product__name a {height: 35px; overflow:hidden;}
.product__amount {color: #000; padding-bottom: 0px;position: absolute; bottom: 10px; z-index:2;}
.action-red.action.font-13{width: 20px; overflow:hidden; height:20px; position:absolute; top:218px;}
.grey.font-11{text-align:right; display: block;}
.product__quantity{display:none; position: absolute; top:172px; background: white;}
.product__quantity-price{margin-bottom:10px;}
.base_product:hover .product__quantity{display:block; padding-bottom:10px}
.last-minute-badge{display:none;}	
}
