css:
@-moz-document url-prefix("http://www.windows93.net/trollbox") {
:root {
	--background: url(https://cdn.discordapp.com/attachments/373317215115542538/424629710421753866/Firefox_wallpaper.png);
}
#trollbox_scroll,
.ui_group > .trollbox_msg {
	border: none !important;
}
.skin_base,
#trollbox_input,
#trollbox_scroll,
#trollbox_infos,
[type=button],
button {
	background: transparent !important;
}
#trollbox::before {
	content: "";
	width: 100%;
	height: 100%;
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background-image: var(--background);
	background-size: cover;
	background-attachment: fixed;
	background-repeat: no-repeat;
	background-position: 50% 50%;
	background-color: #000;
	filter: brightness(50%);
	z-index: -1;
}
textarea,
button,
[type=button] {
	color: white !important;
	height: 3rem;
	border: 1px solid cyan !important;
	border-radius: .5rem;
	box-shadow: none !important;
	margin-top: auto;
	padding: .3rem;
}
textarea {
	border-radius: 0;
	border: 1px solid black !important;
}
body,
html,
#trollbox,
.skin_base,
.trollbox_nick,
textarea,
button {
	font-family: monospace !important;
	font-size: 1.47vh;
}
}
