css:
@-moz-document url-prefix("https://www.cyoc.net/interactives/") {
body {
    background-color: #383632 !important;
}
section {
    background-color: #130e0e !important;
    color: #a59f90 !important;
}
section h3 {
    color: #ced3de !important;
}
section a {
    color: #d9dfe6 !important;
}
element.style {
    color: #fdfbfb !important;
    background-color: #383632 !important;
}
section a:visited {
    color: #bbacb9 !important;
}
.serif {
    color: #bfbfbf !important;
}
.chapter-options a {
    color: #ffe7e7 !important;
}
section h2 {
    background-color: #8a7575 !important;
}
section h4.subtitle {
    background-color: #564747 !important;
}
input {
    background-color: #7da6d6 !important;
}
textarea {
    background-color: #7da6d6 !important;
}
select {
    background-color: #7da6d6 !important;
}
#warning-message {
    background-color: #33332d !important;
}
}
