css:
@-moz-document regexp("(http|https)://(www\\.|)oppressive\\.games/(power).*") {
html
  {
    background: #000 !important;
  }
  
  #main
  {
    background-color: rgba(22, 22, 22, 0.85) !important;
  }
  
  iframe[id^="aswift"]
  {
    display: none !important;
  }
  
  div[style*="height: 20px; background-color: rgba(250, 250, 250, 0.90); position: fixed; width:100%; bottom: 0px;"]
  {
    background: #2C2C2C !important;
    color: #FAFAFA !important;
  }
  
  h1, h2, h3, h4, h5, h6, td, p, center
  {
    color: #FAFAFA !important;
  }
  
  font
  {
    color: #CCC !important;
  }
  
  div[id^=popover], div[id^=popover] > *
  {
    background: #3C3C3C !important;
    color: #FAFAFA !important;
  }
  
  #div1
  {
    background: #3C3C3C !important;
    color: #FAFAFA !important;
  }
  
  img[src="pics/finance.png"], img[src="pics/defense.png"], img[src="pics/health.png"], img[src="pics/labor.png"], img[src="pics/religious.png"], img[src="pics/women.png"], img[src="pics/agriculture.png"], img[src="pics/oil.png"], img[src="pics/politics.png"], img[src="pics/fist.png"], img[src="pics/economy.png"]
  {
    filter: invert(100%);
  }

  img[src="unionlogo/United Farm Workers.png"], img[src="corplogo/U.S. Steel.png"], img[src="corplogo/JPMorgan Chase.png"], img[src="corplogo/Amazon.png"], img[src="corplogo/Apple.png"]
  {
    filter: invert(100%);
  }

  img[src="corplogo/Morgan Stanley.png"]
  {
    filter: invert(100%) hue-rotate(180deg);
  }

  img[src="http://i.imgur.com/ldtmHAx.png"]
  {
    filter: invert(100%);
  }

  .dropdown-menu.show
  {
    background: #3C3C3C !important;
  }
  
  .dropdown-menu.show > a
  {
    color: lightblue !important;
  }
  
  .dropdown-menu.show > a:hover
  {
    background: #2C2C2C !important;
  }
  
  #main2 > center > #svg-id
  {
    filter: invert(100%) hue-rotate(180deg) !important;
  }
  
  .bg-light
  {
    background: #0A0A0a !important;
  }
  
  textarea
  {
    background: #3C3C3C !important;
    color: #FAFAFA !important;
  }

  #main2 > center > .table > tbody > tr > td > .table
  {
    background: #2C2C2C !important;
  }
  
  /*Poll Colors*/
  font[color="blue"]
  {
    color: cornflowerblue !important;
  }
  
  font[color="#A9CCE3"]
  {
    color: lightblue !important
  }
  
  font[color="pink"]
  {
    color: #FF757C !important;
  }
  
  font[color="red"]
  {
    color: #d23f31 !important;
  }
}

@-moz-document regexp("(http|https)://(www\\.|)oppressive-games-power.wikia.com/wiki.*") {
.WikiaPage .WikiaPageBackground
  {
    background: #0A0A0A !important;
  }
  
  aside
  {
    background: #1C1C1C !important;
  }
  
  .SpeechBubble .speech-bubble-message
  {
    background: #1C1C1C !important;
  }
  
  h1, h2, h3, h4, h5, h6, td, p, center
  {
    color: #FAFAFA !important;
  }
  
  font
  {
    color: #CCC !important;
  }
  
  .edited-by, .edited-by > a[class="permalink"], .session
  {
    color: #CCC !important;
  }
  
  .edit-info-user
  {
    color: #FAFAFA !important;
  }
  
  figcaption
  {
    color: #FAFAFA !important;
  }
  
  .pi-data-value.pi-font, #mw-content-text, .edit-info-time
  {
    color: #CCC !important;
  }
  
  .wikitable
  {
    background: #131313 !important;
  }
  
  th[style="text-align: center; background-color:#FFD700"] > font, th[style="text-align: center; background-color:#A9A9A9"] > font
  {
    color: #0A0A0A !important;
  }
  
  .MiniEditorWrapper .loading-indicator .loading-background
  {
    background: #0A0A0A !important;
  }
  
  #bodyContent, #bodyContent > p
  {
    background: #1C1C1C !important;
    color: #FAFAFA !important;
  }
}
