css:
@-moz-document url("https://www.w3schools.com/colors/colors_picker.asp"), url("http://www.w3schools.com/colors/colors_picker.asp") {
body {
    background: #262626;
}

p, span, td {
    color: #aaaab6;
}

.w3-white, .w3-hover-white:hover {
    color: #fff!important;
    background-color: #262626 !important;
}

.nextprev a:link, .nextprev a:visited {
    background-color: #4CAF50;
    color: #fff;
}

.nextprev a {
    border: #4CAF50;
}
.top {
    background: #404040;
    color: #aaaab6 !important;
}

.w3schools-logo {
    color: #fff;
}

.w3-light-grey, .w3-hover-light-grey:hover, .w3-light-gray, .w3-hover-light-gray:hover {
    background: #404040 !important;
}

h2.left {
    color: #aaaab6 !important;
}

a {
    color: #aaaab6;
}

tr {
    background: #404040 !important;
    border: #4d4d4d !important;
}

input {
    background: #404040 !important;
    color: #fff !important;
    border-bottom: none !important;
    border-top: none !important;
    border-right: none !important;
    border-left: 4px solid #404040 !important;
}

#html5colorpicker {
    border: none !important;
}

button {
    background: #4d4d4d !important;
    border: none !important;
	color: #fff !important;
}

h1, h2, h3 {
    color: #aaaab6;
}

#selectedcolor {
    border: none;
}
}
