css:
@-moz-document url-prefix("https://chitchat.ma.pe") {
body, html {
    background: #161617 !important;
}
html, input, textarea {
    color: #d6c6c6 !important;
}
.Dashboard .chatContainer .title {
    background: -webkit-linear-gradient(top,#583232 0,#0a0a0a 100%) !important;
    border-top: .1rem solid #a21717 !important;
    border-bottom: .1rem solid #a21717 !important;
    color: #459bf9 !important;
}
.Message .reasons {
    color: rgba(249, 249, 249, 0.63) !important;
    background: #4e4242 !important;
}
button {
    background: #4e4242 !important;
    color: rgba(249, 249, 249, 0.63) !important;
    border-color: #7d6a6a !important;
}
a {
    color: #459bf9 !important;
}
}
