css:
@media (max-width: 960px) {
	body {
		/* override default min-width: 1020px; */
		
		min-width: 100%;
	}
	/* repo meta info */
	
	.js-repo-meta-container {
		padding: 0 12px;
	}
	/* file list, readme container */
	
	.container {
		width: 100vw;
	}
	/* readme text */
	
	#readme .markdown-body {
		padding: 45px 20px;
	}
	/* issue responds */
	
	.discussion-timeline {
		width: 560px;
	}
}

@media (max-width: 640px) {
	/* hide logo */
	
	.header-logo-invertocat {
		display: none;
	}
	/* header menu column layout */
	
	.Header .HeaderMenu.d-flex {
		flex-direction: column;
	}
	/* header menu align personal info to right */
	
	.Header .HeaderMenu .d-flex {
		justify-content: flex-end !important;
	}
	/* header menu search box width */
	
	.header-search {
		width: 260px;
	}
	/* header menu personnal info */
	
	.user-nav {
		padding-top: 16px;
	}
	/* issue responds */
	
	.discussion-timeline {
		width: 420px;
	}
}
