css:
@-moz-document domain("www.crtv.com") {
body {
    background-color: #000000;
}
:-webkit-full-screen {
    background-color: #000000;
}
.header {
    border-bottom: 2px solid rgba(0, 103, 230, 0.45);
}
.account-menu__trigger,
.header.header-full .wrapper>.account-menu .account-menu__signin,
.header.header-full .header__phone {
    color: #d6d6d6;
}
.container-fluid,
.generic-callout,
.header.header-full {
    background-color: #2a2a2a;
    color: #afafaf;
}
.playlist .thumbnail__title h6 {
    color: #488cf3;
}
.playlist .thumbnail__title .airdate {
    color: #ababab;
}
.hero {
    background-color: transparent;
}
.hero__right {
    display: none;
}
.hero__left {
    width: 100%;
}
.rtf p,
.rtf ul,
.rtf ol,
.rtf .quote {
    color: #e2e2e2;
}
.hero__left .thumbnail__screenshot {
    height: 100%;
}

}
