css:
@document domain(www.fountain.com) {
 /*Structure sidebar*/
 .applicant-sidebar__content{width: 95%;}
 .applicant-sidebar__column_main{width: 100%;}
 .applicant-sidebar__column_secondary{display: none;}
 /*form items*/
 .applicant-profile-data__item{margin: 15px 30px 15px 0; float: left;}
 .applicant-profile-data__link{clear: both; width: 100%; margin: 30px 0;}
 /* action icons */
 .applicant-profile-data__item-icon{display: block !important;}
 /* Edit form */
 .applicant-profile-data__item-edit-controls{top: -6px; right: -20px;}   
 .applicant-profile-data__item-icon_cancel{margin-top:25px;}
 .applicant-profile-data__item-value span{width: 80%;}
}
