css:
@-moz-document domain("atlassian.net")
{
#page-body,
#content *,
.dQwSVN,
.dQwSVN *,
html {
    background-color: #272822;
    color: #ffffff !important;
}
.adg3 .issue-header .ops-cont {
    margin-left: 0
}
.adg3 .issue-header.detached {
    box-shadow: none;
}
#stalker .aui-page-header * {
    color: #F92672 !important;
}
.adg3 #issuetable > thead .active,
.adg3 .issue-table > thead .active {
    background-color: #272822;
}
.adg3 #issuetable > thead .active span,
.adg3 .issue-table > thead .active span {
    color: #FD971F !important;
}
.adg3 #issuetable > thead .sortable:hover,
.adg3 .issue-table > thead .sortable:hover {
    background-color: #3c3d38;
}
.adg3 #issuetable > thead .sortable:hover span,
.adg3 .issue-table > thead .sortable:hover span {
    background-color: #3c3d38;
    color: #FD971F !important;
}
.adg3 #layout-switcher-button:not(.active):not(:active) .aui-icon::before {
    color: #ffffff;
}
}
