css:
@-moz-document domain("www.androidauthority.com") {
/* Main */
html, body, p, div, .header-top-inner2, .block-topic, .content, .article-leaderboard, .article-leaderboard>div, .row, .panels-flexible-region-inside, .region-site-header, #ctBoc, .adunit-wrap, .article-body__press-release, .article-body__aside, .article-body pre, .article-body__critic-reviews, .article-body__reco-block, .article-body__cta-block, .article-body__tabs, .article-body__scroll-box, blockquote.article-body__forum-embed, .article-leaderboard::before {
    background-color: #333 !important;
    color: #999 !important;
    text-align: justify !important;
}
h1, h2, h3, h1.main-title, h1.page-title, .billboard-wrapper h1.main-title, h1.main-title, h1.page-title, .billboard-wrapper h1.main-title {
    color: #999 !important;
}
a {
    color: #fff !important;
    border: 0 !important;
}
.visor-block__header, .taxonomy__header, .search__header {
    border-bottom : solid 2px #999 !important;
}
.header-top__navigation-link, .header-top__search-button {
    background-color: #222 !important;
    color: #999 !important;
}
.block-topic__input, .search__input, .transformSelect>li>span {
    background-color: #222 !important;
    color: #999 !important;
    border: solid 1px #999 !important;
}
.article-leaderboard::before {
    border: 0 !important;
    border-top : solid 1px #999 !important;
}
.shortcodes-header {
 	margin-top: initial !important;   
    color: #999 !important;
}
}
