css:
@-moz-document domain("wuxiaworld.com") {
  
.container 
{
    margin-right: 0;
    margin-left: 0;
    width: 100%;
}
.col-md-8 
{
    width: 100%;
}
body.darkmode {
    background: #000;
}
body.darkmode .main .container {    
    background: #000;
}
body.darkmode .panel-body, body.darkmode .panel-default {
    background: #000000!important;
    color: #fff!important;
}
.header {
    background: #000;
}
.navbar-inverse {
    background-color: #000000;
}
body.darkmode a {
    color: #ffffff!important;
}
.pp .top-bar-area {
    background-color: #252525;
    color: #ffffff;
}
.pp .top-bar-area .btn-link {
    background-color: #252525;
}
.panel-default>.panel-heading {
    color: #ffffff;
}
  
}
