css:
@-moz-document regexp("https?:\\/\\/(www\\.)?watch2gether\\.com\\/rooms\\/.+") {
	#player-chat > div.six.wide.column.tab.chatroom.mobile-tab.tab-active.mobile-tab-chat {
		display: none;
	}

	#player-column {
		width: 80% !important;
		margin-left: auto;
		margin-right: auto;
	}

	.bottom-sticker {
		display: none;
	}

	.ui.menu:not(.vertical) .item>.button {
		font-size: .85714286rem !important;
	}
}
