css:
@-moz-document domain("online.stereosound.co.jp") {
#header-menu,
.banners > a, .banners > a > img,
.widgets,
.wfcontent.is-official-user:not(.prevent-official-user-label) .content-user-official,
body:not(.page-menufeed):not([data-page-tag="連載"]) .wfcontent[data-tags*=" 連載 "] .content-feedlabel > span, body.page-menufeed .newsfeed-block:not([data-newsfeed-block-tag="連載"]) .wfcontent[data-tags*=" 連載 "] .content-feedlabel > span,
.content-author-image,
.content-author-name,
.wfcontent .content-feedlabel,
.is-nativead
{display:none !important;}

#main
{padding-top:60px !important;}

.inserted-spiral .spiral-contents-container, #main-contents-container
{padding-right:0;}

#newsfeed .wfcontent
{padding:10px 0}

#bookmarks-heading .container, #newsfeed .wfcontent, .wfcontent-container
{max-width:1000px;}

.wfcontent.is-official-user:not(.prevent-official-user-label) .content-leading
{padding-top:5px;}

.content-leading.flex-item
{width:20%;}

#newsfeed .wfcontent .content-info
{margin-top:10px;}



.content-next.has-image.loading, .content-cover.has-image,
.image-bg-blur,
.content-body > .sns-shares-simple:first-child,
.sns-shares-simple,
ul.ajaRecommend,
[class*="ajaRecommend"],
.content-pagingad
{display:none !important;}

.content-cover .content-subtitle-above, .content-cover .content-subtitle-below, .content-cover .content-title, .content-cover .content-info, .content-cover .content-tags, .content-cover .content-description, .content > .flex-container, .content-next .content-title, .content-next .content-body, .content-next .content-info, .content-next .content-tags, .content-region, .content-region .widgets
{max-width:1000px;}
}
