css:
@-moz-document domain("workflowy.com")
{

/* GLOBAL FONT STYLING */
.content {
    font-family: Monospace !important;
    color: #333333 !important;
}


/* TAG STYLING */
.contentTag {
    font-family: Courier;
    color: #0489B1 !important;
}


/* LINK STYLING */
.content .contentLink {
    color: lightgray;
    font-family: Courier;
    text-transform: none !important;
}


/* NOTE STYLING */ 
.noted > .notes > .content {
    font-family: Monospace !important;
    color: #333333 !important;
}                          

}
