css:
@-moz-document domain("geek.justjoin.it") {
#cb-outer-container
{
    background: black;
}

#cb-header
{
    background: black;
}

#cb-content
{
    background: black;
}

#cb-container
{
    background: black;
}

#cb-content.wrap.clearfix
{
    background: black;
    color: wheat;
}

.cb-body-light blockquote
{
    color: wheat;
}

.cb-fis h1
{
    color: wheat;
}

#cb-author-box.cb-post-footer-block.cb-post-block-bg.clearfix
{
    background: black;
}

.cb-body-light .cb-article-footer .cb-title,
.cb-body-light .cb-article-footer #reply-title
{
    color: wheat;
}

.cb-body-light #cb-author-box .cb-meta a
{
    color: wheat;
}

#cb-content.wrap.clearfix div.cb-sticky-sidebar aside.cb-sidebar.clearfix
{
    background: black;
}

.cb-body-light h1,
.cb-body-light h2,
.cb-body-light h3,
.cb-body-light h4,
.cb-body-light h5
{
    color: wheat;
}

.cb-body-light .cb-module-block .cb-post-title a
{
    color: wheat;
}

#cb-section-a.cb-site-padding.wrap.cb-hp-section.clearfix
{
    background: black;
}

.cb-module-block .cb-excerpt
{
    color: wheat;
}

#cb-section-b.cb-xtra-pad
{
    background: black;
}

.cb-sidebar-right .cb-sidebar
{
    background: black;
}

#cb-section-c.cb-xtra-pad
{
    background: black;
}

.cb-mm-light #cb-nav-bar .cb-menu-drop h2,
.cb-mm-light #cb-nav-bar .cb-menu-drop a
{
    color: wheat;
}

.cb-mm-light #cb-nav-bar .cb-bg
{
    background: black;
}
}
