css:
@-moz-document url-prefix("https://www.reddit.com/r/gtaonline/") {
  /* Override downvoted state button */
  body.listing-page .arrow.downmod {
      display: block;
      background-image: url("https://b.thumbs.redditmedia.com/Y3OZmVmaANSPoURa.png");
      background-position: 0px 0px;
  }
  
  /* Override default state button */
  #siteTable .arrow.down {
      display: block;
      background-image: url("https://a.thumbs.redditmedia.com/-WF4Gjv9ycpbYGUZ.png");
      background-position: 0px 0px;
  }
}
