css:
@-moz-document url("https://instabart.no/") {
.cardface.front a {
    background: #050505;
}
.no-touch .cardface.front:hover a {
    background: #111
}
.front {
    background: #050505;
    color: rgba(255, 255, 255, .8);
    text-shadow: 1px 4px 6px #354f5d, 0 0 0 white, 1px 4px 6px #050505;
}
.front h2 {
    color: #ffffff;
    text-shadow: 1px 4px 6px #050505, 0 0 0 black, 1px 4px 6px #050505;
}
header {
    background: #0a0a0a;
    border-bottom: 1px solid #222;
}
html {
    background-attachment: fixed;
    background-image: url("https://i.imgur.com/492m0h9.png");
    color: #ffffee;
}
i {
    color: rgba(255, 255, 255, .8);
    text-shadow: 1px 4px 6px #354f5d, 0 0 0 white, 1px 4px 6px #050505;
}
.modal-section {
	border: 1px solid #111;
    border-radius: 10px;
}
.modal-section .header {
    background-color: #050505;
    border-bottom: 1px solid #222;
}
#schedule_settings {
    background-color: rgba(0, 0, 0, .8);
}
.svg #logo {
    -webkit-filter: invert(100%);
    filter: invert(100%);
}
}
