css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("packages.ubuntu.com") {

  body {
    background-color: #222;
    background-image: none;
    color: #999;
  }
  
  #wrapper {
    background-color: #222;
    box-shadow: 0 2px 2px 0 #333;
  }
  
  #header {
    opacity: 0.7;
  }
  
  #pnavbar {
    background-color: #282828;
  }
  
  #footer {
    background-color: #282828;
  }
  
  input[type="text"], textarea {
    background-color: #282828;
    border-color: #333;
  }
  
  input[type="text"]:focus, textarea:focus {
    border-color: #666;
  }
  
  /***** Individual Package Page e.g. packages.ubuntu.com/trusty-updates/clang-3.9 *****/
  
  /* Box on right */
  #pmoreinfo {
    border-color: #444;
  }
  
  /* Other related packages, on top */
  #pdeplegend, #pdownload table {
    border-color: #444;
  }
  
  /* Tables */
  #pdownload td, #pdownload th {
    border-color: #444;
  }
  
  /* For blank-screenshots */
  #screenshot img {
    filter: invert(86%);
  }

}
