css:
@-moz-document domain("workflowy.com")
{



/* ======= CUSTOM COLORED LINKS ======= */

/* WORKFLOWY INTERNAL LINKS */
a[href*="workflowy.com/#"] {
  color: purple !important;
  font-weight: bold !important;
  text-decoration: none !important;
}

/* WORKFLOWY SHARED LINKS */
a[href*="workflowy.com/s/"] {
  color: red !important;
  font-weight: bold !important;
  text-decoration: none !important;
}

/* WORKFLOWY EMBEDDED LINKS */
a[href*="workflowy.com/embed/"] {
  color: gold !important;
  font-weight: bold !important;
  text-decoration: none !important;
}

/* DROPBOX */
a[href*="dropbox.com/s/"] {
  color: green !important;
  font-weight: bold !important;
  text-decoration: none !important;
}

/* WORKFLOWY BLOG */
a[href*="blog.workflowy.com"] {
  color: black !important;
  font-weight: bold !important;
  text-decoration: none !important;
}

/* GOOGLE PHOTOS */
a[href*="photos.app.goo.gl"] {
  color: steelblue !important;
  font-weight: bold !important;
  text-decoration: none !important;
}

/* USERSTLES(DOT)ORG */
a[href*="userstyles.org"] {
  color: #bd2475 !important;
  font-weight: bold !important;
  text-decoration: none !important;
}

/* YOUTUBE */
a[href*="youtu"] {
  color: #dc091b !important;
  font-weight: bold !important;
  text-decoration: none !important;
}



/* TRUNCATE A LINK WHEN NOT HOVERED  */
.content .contentLink:not(:hover), .nameAnimated .contentLink:not(:hover) {
	max-width: 185px;
	overflow: hidden;
	text-overflow: ellipsis;
	vertical-align: bottom; 
	white-space: nowrap;
	display: inline-block !important;
}              

}
